let message = msg.payload;
let topic = msg.topic;
let score = 100;
let errors = [];
msg.valid = true;

// Check if topic is correct

if ( ! ( topic.match('^origin/a.*$') || topic.match('^cache/a.*$') ) ) reject ("topic in incorrect");

// Check existence of id
const id = message.id;

if (id === undefined) reject ("id is undefined") ;
else {
    const valid_uuid = isUUID (id);
    if ( valid_uuid == false ) {
        score = score - 10;
    }
}

const version = message.version;
if (version === undefined || version != "v04") reject ("version is undefined or not correct");

const message_type = message.type;
if (message_type === undefined || message_type != "Feature") reject ("feature is undefined or not correct");

const geometry = message.geometry;
if (geometry === undefined ) reject ("geometry is undefined");
else {
    if ( geometry === null ) {}
    else {
        let geometry_type = geometry.type;
        if ( geometry_type == "Point") {
            let geometry_coord = geometry.coordinates;
            if ( isArray( geometry_coord ) ) {
                if ( geometry_coord.length > 3 ) reject ("geometry coordinates length is not correct");
                else {
                    if ( ! ( ( typeof(geometry_coord[0]) == 'number' ) && ( geometry_coord[0] >= -180 ) && ( geometry_coord[0] <= 180 ) ) ) reject ("geometry latitude is not correct");
                    if ( ! ( ( typeof(geometry_coord[1]) == 'number' ) && ( geometry_coord[1] >= -90 ) && ( geometry_coord[1] <= 90 ) ) ) reject ("geometry latitude is not correct");
                }
            }
            else reject ("geometry is not an array");
        }
        else {
            if ( geometry_type == "Polygon") {
                let geometry_coord = geometry.coordinates[0];
                let point;
                if ( isArray( geometry_coord ) ) {
                    const first = geometry_coord[0];
                    const last = geometry_coord[geometry_coord.length - 1];
                    if ( (! isArray( first ) ) || ( !isArray( last ) ) || ( first[0] != last[0] ) || ( first[1] != last[1] ) ) reject ("Polygon coordinates are not correct");
                    for (let i = 0; i < geometry_coord.length; i++) { 
                        point = geometry_coord[i];
                        if ( isArray( point ) ) {
                            if ( point.length != 2 ) reject ("Point geometry coordinates length is not correct");
                            else {
                                if ( ! ( ( typeof(point[0]) == 'number' ) && ( point[0] >= -180 ) && ( point[0] <= 180 ) ) ) reject ("Point geometry latitude is not correct");
                                if ( ! ( ( typeof(point[1]) == 'number' ) && ( point[1] >= -90 ) && ( point[1] <= 90 ) ) ) reject ("Point geometry latitude is not correct");
                            }
                        }
                        else reject ("Point geometry is not an array");
                    }
                }
                else reject ("geometry is not an array");
            }
            else reject ("geometry is not one of the agreed type (null, point, polygon");
        }
    }
}

const properties = message.properties;
if (properties === undefined) reject ("properties is undefined");
else {

    const integrity = properties.integrity;
    if (integrity === undefined) score = score - 10;
    else { 
        const method = integrity.method;
        if ( method === undefined) reject("Hashing method is undefined");
        else { 
            if ( method == "MD5" ) score = score - 10;
            else if ( method.match('^[sS][hH][aA]256|512$') === null ) reject("Hashing method is not correct");
        }
        const value = integrity.value;
        if ( value === undefined) reject("value is undefined");
    }

    const content = properties.content;
    if (content !== undefined) {
        const encoding = content.encoding;
        if ( encoding === undefined) reject("encoding is undefined");
        else if ( encoding.match('^utf\-8|base64$') === null ) reject("encoding is not correct");
        const value_encoding = content.value;
        if ( value_encoding === undefined) reject("value within encoding is undefined");
        const size = content.size;
        if ( size === undefined) reject("size within encoding is undefined");
        else if ( size > 4096 ) reject("length exceeds the agreed value (4096)");
    }

    const data_id = properties.data_id;
    if ( data_id === undefined) reject("data_id is undefined");
    else if ( data_id.match(topic.substring(topic.indexOf("wis"))) === null ) reject("data_id is not correct");

    const pubtime = properties.pubtime;
    if ( pubtime === undefined) reject("pubtime is undefined");
    else if ( pubtime.match('^[0-9]{4}-[0-9]{2}-[0-9]{2}[t|T| ][0-9]{2}:[0-9]{2}:[0-9]{2}\.?[0-9]*[Z|z]$') === null ) reject("pubtime is not RFC3339 compliant");

    const datetime = properties.datetime;
    const start_datetime = properties.start_datetime;
    const end_datetime = properties.end_datetime;

    if ( datetime === undefined) {
        if ( start_datetime === undefined ) {
            if ( end_datetime === undefined ) score = score - 10;
            else reject("end_datetime is defined but start_datetime is not") ;
        }
        else {
            if ( start_datetime.match('^[0-9]{4}-[0-9]{2}-[0-9]{2}[t|T| ][0-9]{2}:[0-9]{2}:[0-9]{2}\.?[0-9]*[Z|z]$') === null ) reject("start_datetime is not RFC3339 compliant");
            else {
                if ( end_datetime === undefined ) reject("start_datetime is defined but end_datetime is not");
                else if ( end_datetime.match('^[0-9]{4}-[0-9]{2}-[0-9]{2}[t|T| ][0-9]{2}:[0-9]{2}:[0-9]{2}\.?[0-9]*[Z|z]$') === null ) reject("end_datetime is not RFC3339 compliant");
            }
        }
    }
    else {
        if ( start_datetime !== undefined || end_datetime !== undefined ) reject("datetime and start_datetime/end_datetime are both defined");
        if ( datetime.match('^[0-9]{4}-[0-9]{2}-[0-9]{2}[t|T| ][0-9]{2}:[0-9]{2}:[0-9]{2}\.?[0-9]*[Z|z]$') === null ) reject("datetime is not RFC3339 compliant");
    }
}

const links = message.links;
if (links === undefined) reject ("links is undefined");
else {
    if ( isArray( links ) ) {
        let href, rel, mime_type;
        let canonical_link = false;
        for (let i = 0; i < links.length; i++) { 
            href = links[i].href;
            if ( href === undefined) reject ("href is undefined");
            if ( ! ( href.match('^http:\/\/.*$') || href.match('^https:\/\/.*$') || href.match('^sftp:\/\/.*$') || href.match('^ftp:\/\/.*$')) ) reject ("Download protocol is incorrect");
            if ( href.match('^http:\/\/.*$') || href.match('^ftp:\/\/.*$') ) score = score - 20;
            rel = links[i].rel;
            if ( rel === undefined) reject ("rel is undefined");
            if ( rel.match('^canonical$') ) canonical_link = true ;
            else if ( ! ( rel.match('^alternate|via$') ) ) reject ("rel is not correct");
            mime_type = links[i].type;
            if ( mime_type === undefined) reject ("Mime-type is undefined");
        }
        if ( canonical_link == false ) reject ("canonical link is missing");
    }
    else reject ("links is not an array");
}

msg.score = score;

if ( errors.length != 0 ) {
   let err = { "errors": errors };
   msg.payload._comment = err;
} 


return msg;

function isUUID ( uuid ) {
    let s = "" + uuid;
    s = s.match('^[0-9a-fA-F]{8}-[0-9a-fA-F]{4}-[0-9a-fA-F]{4}-[0-9a-fA-F]{4}-[0-9a-fA-F]{12}$');
    if (s === null) {
      return false;
    }
    return true;
}

function reject ( reason ) {
      errors.push(reason);
      score = 0;
      msg.score = score;
      msg.valid = false;
      return msg;
}

function isArray( myArray ) {
  return myArray.constructor.toString().indexOf("Array") > -1;
}

let min = "zzzzzzzz-zzzz-zzzz-zzzz-zzzzzzzzzzzz"
let arr = msg.payload;
let i;
let j=0;
let del=[];
let priority="secondary";

for (i = 0; i < arr.length - 1; i = i + 2) {
    if ( (arr[i] < min) && ( ( Date.now() - parseFloat(arr[i+1]) ) < 8000 ) ) min = arr[i] ;
    if ( (Date.now() - parseFloat(arr[i + 1])) >= 8000 ) {
        del[j] = arr[i];
        j=j+1;
    }
}

if ( flow.get("uuid") == min ) priority="primary"

msg.centre_id=flow.get("centreid");
msg.payload = priority;
msg.del = del;
msg.uuid = flow.get("uuid");

return msg;
